INSERT INTO `services` VALUES (1,'المنازل الذكية','التحكم في جميع وظائف المنزل بالجوال (إنارة، تكييف، شترات وستائر وغيرها).','<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">إن مفهوم أنظمة المنازل الذكية مفهوم عصري وحديث، ويعني التحكم في وظائف المنزل أو مراقبتها عن طريق الجوال من أي مكان في العالم، بشرط أن يكون كل من الجوال ونظام المنزل الذكي على اتصال بالإنترنت.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ومن فوائد أنظمة المنازل الذكية:</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">التحكم بوظائف المنزل من خلال تطبيق الجوال ومنها</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">تهيئة المنزل بالظروف المناسبة قبل الوصول الى المنزل. مثلا (تشغيل التكييف، تشغيل الإنارة .. إلخ).</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ترشيد استهلاك الكهرباء من خلال مراقبة أجهزة المنزل من الجوال وإيقاف تشغيل كل ما هو غير ضروري.</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">اضافة جانب الراحة والرفاهية.</span></span></span></span></li>\r\n</ul>','<ul>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">التحكم في الإنارة العادية والديمر (القابلة للتخفيف).</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">التحكم في التكييف تحكم كامل.</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">التحكم في الستائر الداخلية وشترات الألمونيوم.</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">التحكم الكامل في تشغيل الصوتيات في جميع انحاء المنزل بالإضافة الي المسرح المنزلي.</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">إمكانية منح نظام المنزل الذكي خاصية التصرف الذاتي في بعض وظائف المنزل. مثل (إغلاق شترات الألمنيوم في حالة الغبار أو الامطار، إغلاق الستائر في حالة ارتفاع درجة الحرارة ... إلخ).</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">عمل سيناريوهات للتحكم في عدد من وظائف المنزل بضغطة زر واحد في الجوال. مثل (تشغيل الإنارة مع التكييف في نفس الوقت).</span></span></span></span></li>\r\n</ul>',1,1,'2020-07-28 07:06:33','2020-07-28 07:09:40','المنازل-الذكية'),(2,'كاميرات المراقبة','العادية والشبكية، الداخلية والخارجية، السلكية واللاسلكية.','<ol>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">تعريف بكاميرات المراقبة:</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">الهدف الأساسي لكاميرات المراقبة هو مراقبة المكان الذي يطلبه العميل بما فيها غرف الأطفال وأماكن لعبهم. وتعتبر كاميرات المراقبة بمختلف أنواعها تؤدي هذا الغرض حسب القدر المطلوب، إلا في بعض الحالات الخاصة قد يتطلب الأمر أنواع خاصة من الكاميرات كما هو الحال مع المرور في كاميرات ساهر، ومع كورونا والكاميرات الحرارية.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ومن المعلومات الخاطئة والشائعه بكثرة هي: الاعتقاد بأن الكاميرا ذات المواصفات العالية هي الكاميرا الأفضل للشخص بشكل مطلق.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">قد يكون هذا الاعتقاد صحيح، ولكن الأهم هو معرفة الغرض المطلوب مراقبته ومن ثم تحديد نوع الكاميرا المطلوبة ودقتها بناءاً على ذلك، حتى لا يتكلف العميل مبالغ إضافية في كاميرا غالية الثمن في&nbsp; حين أن من الممكن أن تكون الكاميرا الأقل ثمنا تفي بالغرض تماماً.</span></span></span></span></p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:right\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:right\">&nbsp;</p>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:right\">&nbsp;</p>\r\n\r\n<ol start=\"2\">\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">أنواع كاميرات المراقبة:</span></span></strong></span></span></li>\r\n</ol>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0.5in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">بشكل عام، تنقسم الكاميرات من حيث <u>مكان الاستخدام</u> الي نوعين أساسيين هما:</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">كاميرات داخلية: وهي كاميرات يتم تركيبها داخل المباني، وبالتالي يكون قدرة تحملها للظروف الطبيعية الخارجية ضعيف.</span></span></span></span></li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">كاميرات خارجية: وهي كاميرات يتم تركيبها خارج المباني، وبالتالي هي مصممة لتتحمل درجات الحرارة العالية والظروف الطبيعية القاسية.</span></span></span></span></li>\r\n</ol>\r\n\r\n<p dir=\"RTL\" style=\"margin-left:0in; margin-right:0in; text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">أما من حيث <u>نوع التقنية</u>، فهي تنقسم الي قسمين ايضا:</span></span></span></span></p>\r\n\r\n<ol>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">الكاميرات الرقمية</span></span></strong> <strong><span dir=\"LTR\" style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Analog</span></span></strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">، بالنسبة لكاميرات شركة هيكفيجن فإن الدقة المتوفرة منها ما يلي:</span></span></span></span>\r\n\r\n	<ul>\r\n		<li style=\"list-style-type:none\">\r\n		<ul style=\"list-style-type:square\">\r\n			<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">دقة 2 ميجابكسل.</span></span></span></span></li>\r\n			<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">دقة 5 ميجابكسل.</span></span></span></span></li>\r\n			<li dir=\"RTL\" style=\"text-align:right\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">دقة 8 ميجابكسل.</span></span></span></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li dir=\"RTL\" style=\"text-align:right\"><